import {
  Address,
  concat,
  decodeAbiParameters,
  encodeAbiParameters,
  encodeDeployData,
  hashMessage,
  hashTypedData,
  Hex,
  parseAbiParameters,
  PublicClient,
  SignableMessage,
} from "viem";
import { signMessage } from "viem/accounts";
import { getBytecode } from "viem/actions";
import { accountFactoryAddress, erc1271InputGeneratorAbi } from "../generated";
import { authenticatorData, client } from "../scripts/base";
import { buildWebAuthnSignature, p256WebAuthnSign } from "./signature";
import { createAccountCalldata } from "./smartWallet";

// ERC-6492 magic bytes
export const magicBytes = "0x6492649264926492649264926492649264926492649264926492649264926492";
// deploy code of https://github.com/coinbase/smart-wallet/blob/main/src/utils/ERC1271InputGenerator.sol
const ERC1271InputGeneratorByteCode =
  "";

/// factory and factoryCallData do not need to be passed if account is already deployed on the chain
export const mockWebAuthnERC1271CompatibleEIP191Sign = async (
  client: PublicClient,
  { account, message, p256PrivateKey, factory, owners }: {
    account: Address;
    message: SignableMessage;
    p256PrivateKey: any;
    factory?: Address;
    owners?: Hex[];
  },
) => {
  const hash = hashMessage(message);
  const factoryCalldata = owners ? createAccountCalldata({ owners, nonce: 0n }) : "0x";
  const toSign = await replaySafeHash(client, {
    hash,
    account,
    factory: factory || accountFactoryAddress,
    factoryCalldata,
  });

  const { r, s, clientDataJSON } = p256WebAuthnSign({
    challenge: toSign,
    authenticatorData,
    p256PrivateKey,
  });

  var signature = buildWebAuthnSignature({
    ownerIndex: 0n,
    authenticatorData,
    clientDataJSON,
    r,
    s,
  });

  const accountBytecode = await getBytecode(client, { address: account });
  if (!accountBytecode) {
    signature = concat([
      encodeAbiParameters(
        parseAbiParameters(
          "address accountFactory, bytes createAccountCalldata, bytes originalSignature",
        ),
        [factory || accountFactoryAddress, factoryCalldata, signature],
      ),
      magicBytes,
    ]);
  }

  return signature;
};

/// NOTE Ethereum addresses in `owners` should be abi.encode to 32 bytes
/// factory and factoryCallData do not need to be passed if account is already deployed on the chain
export const replaySafeHash = async (
  client: PublicClient,
  { hash, account, factory, factoryCalldata }: {
    hash: Hex;
    account: Address;
    factory?: Address;
    factoryCalldata?: Hex;
  },
) => {
  const data = encodeDeployData({
    bytecode: ERC1271InputGeneratorByteCode,
    abi: erc1271InputGeneratorAbi,
    args: [account, hash, factory || accountFactoryAddress, factoryCalldata || "0x"],
  });

  const { data: safeHash } = await client.call({ data });
  if (!safeHash) throw new Error("failed to fetch replay safe hash");

  return safeHash;
};

export function isERC6492Signature(signature: string) {
  const ERC6492_DETECTION_SUFFIX = "6492649264926492649264926492649264926492649264926492649264926492";
  return signature.slice(signature.length - 64, signature.length) == ERC6492_DETECTION_SUFFIX;
}

export type ParseERC6492ReturnType = {
  sigToValidate: Hex;
  factory?: Address;
  factoryCalldata?: Hex;
};

export function parseERC6492Signature(signature: Hex): ParseERC6492ReturnType {
  if (!isERC6492Signature(signature)) return { sigToValidate: signature };

  const [factory, factoryCalldata, sigToValidate] = decodeAbiParameters(
    [
      { type: "address" },
      { type: "bytes" },
      { type: "bytes" },
    ],
    signature,
  );
  return { sigToValidate, factory, factoryCalldata };
}
